.header {
    position: fixed;
    z-index: 1024;
    width: 100%;
    display: flex;
    top: 1rem;

    &__logo {
        display: flex;
        position: relative;
        padding: $spaceS;
        z-index: 16;
        transition: 0.5s $bezierFastoutSlowin;
        cursor: pointer;

        &:hover {
            transform: scale(1.1);


            svg path {
                fill: $rgbRoseMadder;
            }
        }

        &:active {
            transform: scale(0.9);
        }

        svg {
            width: 3rem;
            height: 3rem;
        }
    }



    .nav-toggle {
        position: fixed;
        top: $space2XL;
        right: $spaceXL;
        z-index: 1024;
        display: none;
        background: none;
        border: 0;
        outline: 0;
        height: 48px;
        align-items: center;

        svg {
            color: $rgbWhite08;
            fill: $rgbWhite08;
            transition: $bezierFastoutSlowin 0.5s;
            top: 50%;
            left: 50%;
            position: absolute;
            width: 32px;
            height: 32px;
        }

        &__inner {
            position: relative;
        }

        &__icon--menu {
            opacity: 1;
            transform: translate(-50%, -50%);
        }

        &__icon--close {
            opacity: 0;
            transform: translate(-50%, -50%) rotate(45deg);
        }
    }


    .button-icon-only {
        // $buttonPadding: 0;
        // $buttonTextColor: $colorTextBody;
        // width: $buttonSize;
