        align-items: center;
        justify-content: flex-end;
    }

    &__nav-list {
        // transform: rotate(-90deg) translate3d(-50%, 0, 0);
        display: flex;
        // flex-direction: row-reverse;
    }

    &__nav-link {
        padding: $spaceM;
        // color: $rgbWhite08;
        color: $backgroundBlue07;
        font-weight: $fontWeightMedium;
        font-size: initial;
        text-decoration: none;
        position: relative;
        transition: color $durationS ease .1s;
        line-height: 1;

        &:active,
