            width: 100%;
        }

        &__info-container {
            position: initial;
            right: initial;
            top: initial;
            z-index: 3;
        }

        &__form-container {
            padding: 40px 16px;
            margin: 5rem auto auto auto;
        }
    }

    .map {
        // margin-top: 50vh;
        height: 70vh;
        bottom: initial;
        background-size: contain;
        position: relative;
        z-index: 3;
    }

    .footer {
        margin-top: -1rem;
    }

    .footer__container {
        margin-left: 1rem;
        margin-right: 1rem;
        width: calc(100% - 2rem);

    }
}

@media (min-width:768px) and (max-width:1200px) and (orientation:portrait) {
    .projects {
        padding-bottom: 85px;
    }

    .news {
        margin-top: 0;
    }

    .contact {
        &__bottom-rectangle {
            top: -70%;
            height: 130%;
        }
    }
}

.dg.ac {
    z-index: 9999 !important;
}
